function updateDamUrl() {
    var baseURL = 'https://dev-hcltx.et.ae/dx/api/dam/v1/collections/';
    var resultToModify = resultToJSON(); // assuming this returns parsed response JSON

    var groupedModules = [];

    resultToModify.records.forEach(function(record) {
        var time_remaining = record.time_remaining;
        var category_name = record.category_name;
        var content_type = record.content_type;
        var max_bid_amount = record.max_bid_amount;
        var highest_bidder = record.highest_bidder;
        var year = record.year;
        var bids = record.bids;
        var sub_category_name = record.sub_category_name;
        var auction_id = record.auction_id;
        var ID = record.ID;
        var object_id = record.object_id;
        var thumbnail_url = record.thumbnail_url;

        var url = "";
        var mimetype = "";

        if (content_type && content_type.indexOf("/") !== -1) {
            mimetype = "." + content_type.split("/")[1];

            if (thumbnail_url) {
                url = baseURL + "AWA_Thumbnails/items/thumbnail_" + object_id + mimetype + "?binary=true";
            }
        }

        groupedModules.push({
            time_remaining: time_remaining,
            category_name: category_name,
            max_bid_amount: max_bid_amount,
            highest_bidder: highest_bidder,
            year: year,
            bids: bids,
            sub_category_name: sub_category_name,
            auction_id: auction_id,
            ID: ID,
            object_id: object_id,
            content_type: content_type,
            thumbnail_url: url
        });
    });

    return {
        auction_list: groupedModules
    };
}

// Run the function
updateDamUrl();
